export const FlagIcon: React.FC<{lang: string} & FlagIconProps> = ({ lang, className, title }) => {
  switch (lang) {
    case 'en':
      return <USAFlag className={className} title={title} />;
    case 'es':
      return <SpainFlag className={className} title={title} />;
    case 'he':
      return <IsraelFlag className={className} title={title} />;
    case 'ru':
      return <RussiaFlag className={className} title={title} />;
    case 'zh':
      return <ChinaFlag className={className} title={title} />;
    default:
      return <USAFlag className={className} title={title} />;
  }
};

export default FlagIcon; 